sourcesContent:
import {model, Schema} from 'mongoose'

const studentScheme = new Schema({
    name: String,
    lastname: String,
    register: {
        type: String,
        unique: true
    },
    email: {
        type: String,
        unique: true
    },
    codeRfid: String,
    courses: [{
        type: Array,
    }],
    attendance: [{
        ref: "Attendance",
        type: Schema.Types.ObjectId
    }],
    user: [{
        ref: "User",
        type: Schema.Types.ObjectId
    }],
}, {
    timestamps: true,
    versionKey: false
})

export default model('Student', studentScheme)

